class VideoContainer extends HTMLElement {
    connectedCallback() {
        this.attachShadow( { mode: 'open' } )
            .innerHTML = `<div class="iframe-container">
            <iframe class="video" width="100%" height="1005" src="${this.getAttribute('url')}" frameborder="0" allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture" allowfullscreen>
            </iframe>
        </div>` 
    }
}

class HeroImage extends HTMLElement {
    connectedCallback() {
        this.innerHTML = 
        `<section class="cover">
            <div class="mainimage">
                <img src="${this.getAttribute('url')}" alt="Hero Image">
            </div>
        </section>`
    }
}

class TitleText extends HTMLElement {
    
    connectedCallback() {

        let title = this.getAttribute('title')
        document.querySelector('meta[name="description"]').content = title
        document.title = `${title} | ASD GradShow 2021`



        this.innerHTML = 
        `<div  class="titletext">
        ${title}
        </div>`
    }
}


class ProjectBio extends HTMLElement {
    connectedCallback() {

        let mainTitle = this.getAttribute('main-title')
        let summary = this.getAttribute('summary')
        let aboutme = this.getAttribute('aboutme')
        let studentTitle = this.getAttribute('student-title')
        let name = this.getAttribute('name')
        let portrait = this.getAttribute('portrait')
        let mentor = this.getAttribute('mentor')
        let typology = this.getAttribute('typology')
        let location = this.getAttribute('location')
        let email = this.getAttribute('email')
        let issuu = this.getAttribute('issuu')
        let website = this.getAttribute('website')
        let instagram = this.getAttribute('instagram')
        let linkedin = this.getAttribute('linkedin')

        this.innerHTML = 
        `<div class="twocols invert">
        <div class="abstract">
            <h2>${mainTitle}</h2>
            ${summary}
            <br><br>
            <h4>${studentTitle}</h4>
            ${aboutme}
        </div>

        <div class="table">
            <div class="thumb">
                <img src="${portrait}" alt="Student Image"></div>
                <div class="fields">
                <h3>Name</h3>
                <p>${name}</p>
                </div>
                <div class="fields">
                </div>
                <div class="fields">
                <h3>Mentor</h3>
                <p>${mentor}</p>
                </div>
                <div class="fields">
                <h3 >Typology</h3>
                <p>${typology}</p>
                </div>
                <div class="fields">
                <h3>Site Location</h3>
                <p>${location}</p>
                </div>


                    <div class="contact">
                        ${  email === ""? "" :`<a href="mailto:${email}">                            
                        <img class="icon" src="data:image/svg+xml;base64," alt="Email me">
                        </a>`
                        }

                        ${ issuu === ""? "" :`
                        <a href="${issuu}">
                            <img class="icon" src="data:image/svg+xml;base64," alt="My Portfolio">
                        </a>`
                        }

                        ${ website === ""? "" :`
                        <a href="${website}">
                            <img class="icon" src="data:image/svg+xml;base64," alt="My Website">
                        </a>`
                        }

                        ${ instagram === ""? "" :`
                        <a href="${instagram}">
                            <img class="icon" src="data:image/svg+xml;base64," alt="My Instagram">
                        </a>`
                        }

                        ${ linkedin === ""? "" :`
                        <a href="${linkedin}">
                            <img class="icon" src="data:image/svg+xml;base64," alt=""My Linkedin>
                        </a>`
                    }
                    </div>

        </div>
    </div>`
    }
}

